package Java_Programming_Elements;

public class profit_loss {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sellingprice=191;
		int costprice=129;
		System.out.println("The Cost Price is INR"+" "+ 129+" "+ "and Selling Price is INR"+" "+191+" "+ "");
        int p= sellingprice - costprice;
        int p_perc= p/ costprice * 100;
        System.out.println("The Profit is INR" +" "+ p +" "+"and the Profit Percentage is" +" "+p_perc+" ");
        

	}

}
